import{_ as a,c as s,o as i,a1 as e}from"./chunks/framework.t0t3spwR.js";const g=JSON.parse('{"title":"使用 jabba 管理 Java 版本","description":"","frontmatter":{},"headers":[],"relativePath":"article/2024.10/1.md","filePath":"article/2024.10/1.md"}'),t={name:"article/2024.10/1.md"},l=e('<h1 id="使用-jabba-管理-java-版本" tabindex="-1">使用 jabba 管理 Java 版本 <a class="header-anchor" href="#使用-jabba-管理-java-版本" aria-label="Permalink to &quot;使用 jabba 管理 Java 版本&quot;">​</a></h1><h2 id="介绍" tabindex="-1">介绍 <a class="header-anchor" href="#介绍" aria-label="Permalink to &quot;介绍&quot;">​</a></h2><p>jabba 是一个用于管理 Java 版本的工具，它可以帮助你轻松地安装、切换和管理多个 Java 版本。jabba 可以在大多数操作系统上运行，包括 Windows、macOS 和 Linux。</p><h2 id="为什么使用-jabba" tabindex="-1">为什么使用 Jabba? <a class="header-anchor" href="#为什么使用-jabba" aria-label="Permalink to &quot;为什么使用 Jabba?&quot;">​</a></h2><ul><li>方便管理多个 Java 版本:不同项目可能需要不同的 Java 版本,Jabba 让切换变得简单。</li><li>简化安装过程:Jabba 可以自动下载和安装 JDK,无需手动操作。</li><li>跨平台支持:可在 Windows、macOS 和 Linux 上使用。</li><li>支持多种 JDK 发行版:如 Oracle JDK、OpenJDK、Zulu 等。</li></ul><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>github 地址：<a href="https://github.com/shyiko/jabba" target="_blank" rel="noreferrer">https://github.com/shyiko/jabba</a></p><p>安装根据操作系统不同，安装方式也不同，具体可以参考 github 上的安装文档。</p><h2 id="使用" tabindex="-1">使用 <a class="header-anchor" href="#使用" aria-label="Permalink to &quot;使用&quot;">​</a></h2><p>列出远程仓库可以安装的版本列表</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jabba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ls-remote</span></span></code></pre></div><p>安装指定版本</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jabba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> openjdk@17.0.1</span></span></code></pre></div><p>安装最新版本</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jabba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> openjdk@latest</span></span></code></pre></div><p>切换到指定版本</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jabba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> use</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> openjdk@17.0.1</span></span></code></pre></div><p>列出本地安装的版本</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jabba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ls</span></span></code></pre></div><p>卸载指定版本</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jabba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> uninstall</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> openjdk@17.0.1</span></span></code></pre></div><p>设置默认 Java 版本</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">jabba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> alias</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> default</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> 1.8</span></span></code></pre></div>',23),h=[l];function p(n,d,o,b,c,r){return i(),s("div",null,h)}const u=a(t,[["render",p]]);export{g as __pageData,u as default};
