import{_ as a,c as e,o as s,V as t}from"./chunks/framework.AW5yxxpt.js";const g=JSON.parse('{"title":"职业规划底层逻辑","description":"","frontmatter":{},"headers":[],"relativePath":"article/guide/chapter3/3.1.md","filePath":"article/guide/chapter3/3.1.md"}'),i={name:"article/guide/chapter3/3.1.md"},p=t(`<h1 id="职业规划底层逻辑" tabindex="-1">职业规划底层逻辑 <a class="header-anchor" href="#职业规划底层逻辑" aria-label="Permalink to &quot;职业规划底层逻辑&quot;">​</a></h1><p>做职业规划的核心底层逻辑</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>1.工作前十年，尽可能多的做原始资本积累，通俗的说就是存钱</span></span>
<span class="line"><span>2.工作十年后，让资产型收入稳定，扩展收入来源渠道</span></span></code></pre></div><p>基于上面的指导思想，我们的执行思路有有了，除了技术提升以外，还需要其他技能跨界组合，我的副业理论</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>技术 + 产品 + 运营 = 小型印钞机</span></span></code></pre></div><p>在国外，有非常多的小而美的公司，他们往往只有 1-2 个人，但是年产值在几百万美金左右，如果你的收入能稳定在年赚 100 万左右，我觉得这就已经够过上职业自由的生活了，因此，在职业规划上，眼界不要只盯着技术，技术之外的能力，也需要提升</p><h2 id="个人规划" tabindex="-1">个人规划 <a class="header-anchor" href="#个人规划" aria-label="Permalink to &quot;个人规划&quot;">​</a></h2><p>每个人都是独立的个体，能力不同，条件不同，变量很多，具体的职业规划咱们这边没法给出，但是，可以给你做一对一的咨询答疑，有需要咨询的，请加微信</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>微信：Michael-Niuma</span></span></code></pre></div>`,9),n=[p];function c(o,l,d,r,h,_){return s(),e("div",null,n)}const m=a(i,[["render",c]]);export{g as __pageData,m as default};
